from django.utils.datetime_safe import datetimefrom rest_framework import serializersfrom decimal import Decimalfrom goods.models import SKUfrom .models import OrderInfo, OrderGoodsclass CartSKUSerializer(serializers.ModelSerializer):    """订单中的商品序列化器"""    count = serializers.IntegerField(label='商品的购买数量')    class Meta:        model = SKU        fields = ['id', 'name', 'default_image_url', 'price', 'count']class OrderSettlementSerializer(serializers.Serializer):    """订单序列化器"""    skus = CartSKUSerializer(many=True)    freight = serializers.DecimalField(label='运费', max_digits=10, decimal_places=2)class CommitOrderSerializer(serializers.ModelSerializer):    """保存订单序列化"""    class Meta:        model = OrderInfo        fields = ['address', 'pay_method', 'order_id']        read_only_fields = ['order_id']  # 只序列化输出 不做反序列化        extra_kwargs = {            'address': {'write_only': True},            'pay_method': {'write_only': True},        }    def create(self, validated_data):        """保存订单"""        # 获取当前保存订单时需要的信息        # 订单编号: 拿当前时间 + 00001  : 20190414154600 + 000000001        # 获取用户对象        user = self.context['request'].user        # 生成订单编号        order_id = datetime.now().strftime('%Y%m%d%h%M%S') + '%09d' % user.id        # 获取前端传入的收货地址        address = validated_data.get('address')        # 获取前端传入的支付方式        pay_method = validated_data.get('pay_method')        # 订单状态        status = (OrderInfo.ORDER_STATUS_ENUM['UNPAID']                  if pay_method == OrderInfo.PAY_METHODS_ENUM['ALIPAY']                  else OrderInfo.ORDER_STATUS_ENUM['UNSEND'])        # 保存订单基本信息 OrderInfo（一）        orderInfo = OrderInfo.objects.create(            order_id=order_id,            user=user,            address=address,            total_count=0,  # 订单中商品总数量            total_amount=Decimal('0.00'),  # 订单总金额            freight=Decimal('10.00'),            pay_method=pay_method,            status=status        ) 